// CHECK-O2-NEXT:    [[ADD_PTR:%.*]] = getelementptr inbounds nuw i32, ptr [[BUF]], i64 [[IDX_EXT]]
// CHECK-O2-NEXT:    br label %[[FOR_BODY:.*]]
// CHECK-O2:       [[FOR_COND_CLEANUP]]:
// CHECK-O2-NEXT:    ret void
// CHECK-O2:       [[FOR_BODY]]:
// CHECK-O2-NEXT:    [[INDVARS_IV:%.*]] = phi i64 [ 0, %[[FOR_BODY_LR_PH]] ], [ [[INDVARS_IV_NEXT:%.*]], %[[CONT1:.*]] ]
// CHECK-O2-NEXT:    [[ARRAYIDX:%.*]] = getelementptr i32, ptr [[BUF]], i64 [[INDVARS_IV]]
// CHECK-O2-NEXT:    [[TMP0:%.*]] = icmp ult ptr [[ARRAYIDX]], [[ADD_PTR]], {{!annotation ![0-9]+}}
// CHECK-O2-NEXT:    [[TMP1:%.*]] = icmp uge ptr [[ARRAYIDX]], [[BUF]], {{!annotation ![0-9]+}}
// CHECK-O2-NEXT:    [[OR_COND:%.*]] = and i1 [[TMP0]], [[TMP1]], {{!annotation ![0-9]+}}
// CHECK-O2-NEXT:    br i1 [[OR_COND]], label %[[CONT1]], label %[[TRAP:.*]], {{!annotation ![0-9]+}}
// CHECK-O2:       [[TRAP]]:
// CHECK-O2-NEXT:    tail call void @llvm.ubsantrap(i8 25) #[[ATTR3:[0-9]+]], {{!annotation ![0-9]+}}
// CHECK-O2-NEXT:    unreachable, {{!annotation ![0-9]+}}
// CHECK-O2:       [[CONT1]]:
// CHECK-O2-NEXT:    [[TMP2:%.*]] = trunc nuw nsw i64 [[INDVARS_IV]] to i32
// CHECK-O2-NEXT:    store i32 [[TMP2]], ptr [[ARRAYIDX]], align 4, {{!tbaa ![0-9]+}}
// CHECK-O2-NEXT:    [[INDVARS_IV_NEXT]] = add nuw nsw i64 [[INDVARS_IV]], 1
// CHECK-O2-NEXT:    [[EXITCOND_NOT:%.*]] = icmp eq i64 [[INDVARS_IV_NEXT]], [[IDX_EXT]]
// CHECK-O2-NEXT:    br i1 [[EXITCOND_NOT]], label %[[FOR_COND_CLEANUP]], label %[[FOR_BODY]], !llvm.loop [[LOOP9:![0-9]+]]
